using AOC2024.DaySolvers.Day20;

namespace AOC2024.DaySolvers;

public partial class Day20Solver : IDaySolver
{
	public int Day => 20;
	public string Title => "Race Condition";

	public long SolvePart1(string input)
	{
		var trackInput = input.Split(Environment.NewLine).Select(line => line.ToCharArray()).ToArray();
		var track = TrackBuilder.Build(trackInput);

		var time = track.Race();
		var cheatTimes = track.RaceWithCheats();
		var orderedCheatTimes = cheatTimes
			.Where(g => time - g.Key >= Limit)
			.Select(g => (g.Count(), time - g.Key))
			.OrderBy(g => g.Item2)
			.ToList();

		var sum = orderedCheatTimes
			.Where(g => g.Item2 >= Limit)
			.Sum(g => g.Item1);

		return sum;
	}

	public long SolvePart2(string input)
	{
		var trackInput = input.Split(Environment.NewLine).Select(line => line.ToCharArray()).ToArray();
		var track = TrackBuilder.BuildV2(trackInput);

		var time = track.Race();
		var cheatTimes = track.RaceWithCheats();
		var orderedCheatTimes = cheatTimes
			.Where(g => time - g.Key >= Limit)
			.Select(g => (g.Count(), time - g.Key))
			.OrderBy(g => g.Item2)
			.ToList();

		var sum = orderedCheatTimes
			.Where(g => g.Item2 >= Limit)
			.Sum(g => g.Item1);

		return sum;
	}

